import React, { Component, Fragment } from "react";
import './BlogPost.css';
import Post from "../../Component/Post/Post";


class BlogPost extends Component {

    state = {
        post: [],
        formBlogPost:{
            id : 1,
            title: '',
            body:'',
            userId:1
        }
    }

    getPostAPI(){
        fetch('http://localhost:3004/posts',)
        .then(response => response.json())
        .then(json => {
            this.setState({
                post : json
            })
        })
    }
    handleRemove = (data) => {
       fetch('http://localhost:3004/posts/'+data, {
        method: 'DELETE',
        body: JSON.stringify({
          
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8"
        }
      }).then((res) => {
          this.getPostAPI();
      })
    }

    handleFormChange =(event) =>{
        
        let name = event.target.name;
        let value = event.target.value;
        this.setState({ [name]: value });
        
        // let formBlogPostNew = {...this.state.formBlogPost};
        // formBlogPostNew['id'] = timestamp
        // formBlogPostNew[event.target.name] = event.target.value;
        // this.setState({
        //     formBlogPost: formBlogPostNew
        // })
    }

    handleSubmit(){
        let timestamp = new Date().getTime();
        fetch('http://localhost:3004/posts/', {
        method: 'POST',
        body: JSON.stringify({
          
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8"
        }
      }).then((res) => {
          console.log(res);
          this.getPostAPI();
      }, (err) =>{
          console.log('error: ',err);
      })
    }

    componentDidMount() {
        this.getPostAPI();
    }

    render() {
        return (
            <Fragment>
                <p className="section-title" >Blog Post</p>
                <div className="form-add-post">
                    <label htmlFor="title"></label>
                    <input type="text" name="title" onChange={this.handleFormChange} placeholder="add your title"/>
                    <label htmlFor="body">Nlog Content</label>
                    <textarea name="body" id="body" cols="30" rows="10" onChange={this.handleFormChange} placeholder="add blog content"></textarea>
                    <button className="btn-submit" onClick={this.handleSubmit}>Simpan</button>
                </div>
                {
                    this.state.post.map( post => {
                        return <Post key={post.id} data={post} remove={this.handleRemove}/>
                    })
                }
               
            </Fragment>
        )
    }
}

export default BlogPost;
