import './BlogPost.css';
import Post from "../../Component/Post/Post";


class BlogPost extends Component {

    state = {
        post: []
    }

    getPostAPI(){
        fetch('http://localhost:3004/posts',)
        .then(response => response.json())
        .then(json => {
            this.setState({
                post : json
            })
        })
    }
    handleRemove = (data) => {
       fetch('http://localhost:3004/posts/'+data, {
        method: 'DELETE',
        body: JSON.stringify({
          
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8"
        }
      }).then((res) => {
          this.getPostAPI();
      })
    }
    componentDidMount() {
        this.getPostAPI();
    }
    render() {
        return (
            <Fragment>
                <p className="section-title" >Blog Post</p>
                <div className="form-add-post">
                    <label htmlFor="title"></label>
                    <input type="text" name="titile" placeholder="add your title"/>
                    <label htmlFor="body-content">Nlog Content</label>
                    <textarea name="body-content" id="body-content" cols="30" rows="10" placeholder="add blog content"></textarea>
                    <button className="btn-submit"></button>
                </div>
                {
                    this.state.post.map( post => {
                        return <Post key={post.id} data={post} remove={this.handleRemove}/>
                    })
                }
               
            </Fragment>
        )
    }
}

export default BlogPost;
