    //             order: this.state.order - 1,
    //             price: this.state.price - 120000
    //         }, () => {
    //             this.handleCounterChange(this.state.order);
    //         })
    //     }
    // }
    render() {
        return (
            <div className="counter">
                <button className="minus" onClick={() => this.props.state.dispatch({ type: 'MINUS_ORDER' })}>-</button>
                <input type="text" value={this.props.state.totalOrder} />
                <button className="plus" onClick={() => this.props.state.dispatch({ type: 'PLUS_ORDER' })}>+</button>
            </div>
        )
    }
}

export default GobalConsumer(Counter);
