let BaseUrl = 'http://localhost:3004/posts/';


export function RestAPIGet(){
    return new Promise((resolve, reject) => {

        fetch('http://localhost:3004/posts'+type,{
            method: 'GET',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData)
        })
        .then((response) => response.json())
        .then((responsejson) => {
            resolve(responsejson);
        })
        .catch((error) => {
            reject(error);
        })
        // console.log(BaseUrl);
    });
}

export function RestAPIPost(type, userData){

    return new Promise((resolve, reject) => {

        fetch(BaseUrl+type,{
            method: 'POST',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData)
        })
        .then((response) => response.json())
        .then((responsejson) => {
            resolve(responsejson);
        })
        .catch((error) => {
            reject(error);
        })
        // console.log(BaseUrl);
    });
}

