import React, { Component, Fragment } from "react";
import './Product.css';

class Product extends Component {
    state = {
        order: 0
    }


    handlePlus = () => {
        this.setState({
            order: this.state.order + 1
        })
    }

    handleMinus = () => {
        if (this.state.order > 0) {
            this.setState({
                order: this.state.order - 1
            })
        }
    }

    render() {
        return (
            <Fragment>
                <div className="header">
                    <div className="logo">
                        <img src="" />
                    </div>
                    <div className="troley">
                        <img src="https://image.shutterstock.com/image-vector/trolley-logo-shopping-icon-buy-260nw-1103146715.jpg" />
                        <div className="count">{this.state.order}</div>
                    </div>
                </div>
                <div className="card">
                    <div className="img-thumb-prod">
                        <img src="https://s3-ap-southeast-1.amazonaws.com/etanee-images/product/A1101102.jpg" />
                    </div>
                    <p className="product-title">Daging Ayam Segar</p>
                    <p className="product-price">Rp. 120000</p>
                    <div className="counter">
                        <button className="minus" onClick={this.handleMinus}>-</button>
                        <input type="text" value={this.state.order} />
                        <button className="plus" onClick={this.handlePlus}>+</button>
                    </div>
                </div>
            </Fragment>
        )
    }
}

export default Product;
