let BaseUrl = 'http://localhost:3004/posts/';


export function RestAPIGet(type, userData){
    return new Promise((resolve, reject) => {

        fetch('http://localhost:3004/posts')
            .then(response => response.json())
            .then(json => {
                this.setState({
                    post: json
                })
            })
    });
}


export function RestAPIPost(type, userData){

    return new Promise((resolve, reject) => {

        fetch(BaseUrl+type,{
            method: 'POST',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData)
        })
        .then((response) => response.json())
        .then((responsejson) => {
            resolve(responsejson);
        })
        .catch((error) => {
            reject(error);
        })
        // console.log(BaseUrl);
    });
}

export function RestAPIPut(type, userData){
     return new Promise((resolve, reject)=>{
        fetch(BaseUrl+type,{
            method: 'PUT',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData)
        })
        .then((response) => response.json())
        .then((responsejson) => {
            resolve(responsejson);
        })
        .catch((error) => {
            reject(error);
        })
     })
}

export function RestAPIDelete(type, userData){
    return new Promise((resolve, reject)=>{
        fetch(BaseUrl+type,{
            method: 'DELETE',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData)
        })
        .then((response) => response.json())
        .then((responsejson) => {
            resolve(responsejson);
        })
        .catch((error) => {
            reject(error);
        })
     })
}
