import React, { Component, Fragment } from "react";
import './BlogPost.css';
import Post from "../../Component/Post/Post";
import { RestAPIPost } from "../../RestAPIPost";


class BlogPost extends Component {
    state = {
        post: [],
        formBlogPost: {
            id: 1,
            title: '',
            body: '',
            userId: 1
        },
        isUpdate: false
    }

    getPostAPI() {
        fetch('http://localhost:3004/posts?_sort=id&_order=desc')
            .then(response => response.json())
            .then(json => {
                this.setState({
                    post: json
                })
            })
    }
    handleRemove = (data) => {
        fetch('http://localhost:3004/posts/' + data, {
            method: 'DELETE',
            body: JSON.stringify({

            }),
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        }).then((res) => {
            this.getPostAPI();
        })
    }

    handleFormChange = (event) => {
        let timestamp = new Date().getTime();
        let formBlogPostNew = { ...this.state.formBlogPost };
        if (!this.state.isUpdate) {
            formBlogPostNew['id'] = timestamp
        }
        formBlogPostNew[event.target.name] = event.target.value;
        this.setState({
            formBlogPost: formBlogPostNew
        })


    }

    handleSubmit = () => {
        if (this.state.isUpdate) {
            this.putDataAPI();
        } else {
            this.postDataApi();
        }
    }
    postDataApi = () => {
        // fetch('http://localhost:3004/posts/', {
        //     method: 'POST',
        //     body: JSON.stringify({
        //         id: this.state.formBlogPost.id,
        //         title: this.state.formBlogPost.title,
        //         body: this.state.formBlogPost.body,
        //         userId: 1
        //     }),
        //     headers: {
        //         "Content-type": "application/json; charset=UTF-8"
        //     }
        // }).then(response => response.json())
        //     .then(json => {
        //         this.getPostAPI();
        //         this.setState({
        //             formBlogPost: {
        //                 id: 1,
        //                 title: '',
        //                 body: '',
        //                 userId: 1
        //             },
        //             isUpdate: false
        //         })
        //     }, (err) => {
        //         console.log('error: ', err);
        //     })
        this.body = {
            id: this.state.formBlogPost.id,
            title: this.state.formBlogPost.title,
            body: this.state.formBlogPost.body,
            userId: 1
        }
        RestAPIPost('', this.body).then((result) => {
            console.log(result);
        }).then(json => {
            this.getPostAPI();
            this.setState({
                formBlogPost: {
                    id: 1,
                    title: '',
                    body: '',
                    userId: 1
                },
                isUpdate: false
            })
        }, (err) => {
            console.log('error: ', err);
        })
    }
    handleUpdate = (data) => {
        // console.log(data);
        this.setState({
            formBlogPost: data,
            isUpdate: true
        })

    }
    putDataAPI = () => {
        fetch('http://localhost:3004/posts/' + this.state.formBlogPost.id, {
            method: 'PUT',
            body: JSON.stringify({
                id: this.state.formBlogPost.title.id,
                title: this.state.formBlogPost.title,
                body: this.state.formBlogPost.body,
                userId: 1
            }),
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        }).then(response => response.json())
            .then(json => {
                this.getPostAPI();
                this.setState({
                    formBlogPost: {
                        id: 1,
                        title: '',
                        body: '',
                        userId: 1
                    },
                    isUpdate: false
                })
            }, (err) => {
                console.log('error: ', err);
            })
    }

    componentDidMount() {
        this.getPostAPI();
    }

    render() {
        return (
            <Fragment>
                <p className="section-title" >Blog Post</p>
                <div className="form-add-post">
                    <label htmlFor="title"></label>
                    <input type="text" name="title" value={this.state.formBlogPost.title} onChange={this.handleFormChange} placeholder="add your title" />
                    <label htmlFor="body">Nlog Content</label>
                    <textarea name="body" id="body" cols="30" rows="10" value={this.state.formBlogPost.body} onChange={this.handleFormChange} placeholder="add blog content"></textarea>
                    <button className="btn-submit" onClick={this.handleSubmit}>Simpan</button>
                </div>
                {
                    this.state.post.map(post => {
                        return <Post key={post.id} data={post} remove={this.handleRemove} update={this.handleUpdate} />
                    })
                }

            </Fragment>
        )
    }
}

export default BlogPost;
